const qs =
  "utm_campaign=test-campaign&utm_content=test-content&utm_medium=email&utm_source=hubspot";
const search_params = new URLSearchParams(qs).entries();

while (true) {
  const e = search_params.next();
  if (e.value) {
    const [k, v] = e.value;
    document.cookie = k + "=" + encodeURIComponent(v);
  }

  if (e.done) break;
}
